package com.felipevilla.pruebajpa.controller;import com.felipevilla.pruebajpa.model.Persona;import com.felipevilla.pruebajpa.service.IPersonaService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestControllerpublic class PersonaController {    @Autowired    private IPersonaService persoSer;    @GetMapping ("/personas/traer")    public List<Persona> getPersona(){        return persoSer.getPersonas();    }    @PostMapping ("/personas/crear")    public String savePersona(@RequestBody Persona perso){        persoSer.savePersona(perso);        return "La persona fue creada correctamente";    }    @DeleteMapping ("/personas/borrar/{id}")    public String deletePersona(@PathVariable Long id){        persoSer.deletePersona(id);        return "La persona se ha elimnada correctamente";    }    @PutMapping ("/personas/editar/{id_original}")    public Persona editPersonas(@PathVariable long id_original,            @RequestParam(required = false, name = "id") Long nuevaId,            @RequestParam(required = false, name = "nombre") String nuevoNombre,            @RequestParam(required = false, name = "apellido") String nuevoApellido,            @RequestParam(required = false, name = "edad") int nuevaEdad) {        persoSer.editPersona(id_original, nuevaId, nuevoNombre, nuevoApellido, nuevaEdad);        Persona persoEdit = persoSer.findPersona(id_original);        return persoEdit;    }}